import React from "react";

import "../componentes/acordeon.css";

import { Button } from "react-bootstrap"; // Importa el componente Button de react-bootstrap

import Accordion from "react-bootstrap/Accordion";

// Importaciones para carrusel bootstrap con imagenes
import Carousel from 'react-bootstrap/Carousel';
import CarouselCaption from 'react-bootstrap/esm/CarouselCaption';
import ImagenCarrousel1 from '../assets/img/Carrusel1.png';
import ImagenCarrousel2 from '../';

function Acordeon() {
  return (

    // Sesion del contenedor de lado derecha
    <div className="columDerecha">
      <h1 className="textoFullStack">Desarrolladores Full Stack</h1>
      <br />
      ...

      {/* Sesion de slider */}
      <div>
        {/* SESION DE CARRUSEL */}
        <Carousel>
            <Carousel.Item>
                <img src={ImagenCarrousel1} alt="" />
                <CarouselCaption>
                    <h3>Primera imagen</h3>
                    <p><font color ='black'>Segundo curso de programacion orientada a la web</font></p>
                </CarouselCaption>
            </Carousel.Item>

            {/* SEGUNDA SESION DE CARRUSEL */}
            <Carousel.Item>
                <img src={ImagenCarrousel2} alt="" />
                <CarouselCaption>
                    <h3>Segundo imagen</h3>
                    <p>Segundo curso de programacion orientada a la web</p>
                </CarouselCaption>
            </Carousel.Item>
        </Carousel>
      </div>


      <p>Desarrollo del Bootcamp</p>
      {/* Sesion Acordeon */}
      <div className="acordeonPag">
        <Accordion>
          <Accordion.Item eventKey="0">
            <Accordion.Header>
              <h4>Sesión 1-</h4>
              <h5> Jueves, 21 de marzo de 2024</h5>{" "}
              <Button variant="danger" size="sm" disabled>
                Finalizado
              </Button>
            </Accordion.Header>

            <Accordion.Body>
              <b>Horario de la sesión:</b>
              <br />
              jueves, 21 de marzo de 2024 a las 5:55 AM
              <br />
              <b>Descripción:</b>
              Descripción de la Sesión 1<br />
            </Accordion.Body>
          </Accordion.Item>
        </Accordion>
      </div>
    </div>
  );
}
export default Acordeon;
